mapper.insert:
insert into SYS_APPLICATION

	ID

		,NAME


		,NAMEPINYIN


		,CODE


		,APPDESC


		,DISCRIMINATOR


		,TREEID


		,ICON


		,ICONCLS


		,LEVEL


		,URL


		,SORTNO


		,SHOWMENU


		,SHOWTYPE


		,SYSFLAG_


		,PARENTID


		,LOCKED


		,IMAGEPATH


		,LINKTYPE


		,CREATEDATE


		,CREATEBY


		,UPDATEBY


		,UPDATEDATE


		,DELETEFLAG


		,DELETETIME




	#{id, jdbcType=BIGINT}


		,#{name, jdbcType=VARCHAR}


		,#{namePinyin, jdbcType=VARCHAR}


		,#{code, jdbcType=VARCHAR}


		,#{desc, jdbcType=VARCHAR}


		,#{discriminator, jdbcType=VARCHAR}


		,#{treeId, jdbcType=VARCHAR}


		,#{icon, jdbcType=VARCHAR}


		,#{iconCls, jdbcType=VARCHAR}


		,#{level, jdbcType=INTEGER}


		,#{url, jdbcType=VARCHAR}


		,#{sort, jdbcType=INTEGER}


		,#{showMenu, jdbcType=INTEGER}


		,#{showType, jdbcType=VARCHAR}


		,#{sysFlag, jdbcType=VARCHAR}


		,#{parentId, jdbcType=BIGINT}


		,#{locked, jdbcType=INTEGER}


		,#{imagePath, jdbcType=VARCHAR}


		,#{linkType, jdbcType=VARCHAR}


		,#{createDate, jdbcType=TIMESTAMP}


		,#{createBy, jdbcType=VARCHAR}


		,#{updateBy, jdbcType=VARCHAR}


		,#{updateDate, jdbcType=TIMESTAMP}


		,#{deleteFlag, jdbcType=INTEGER}


		,#{deleteTime, jdbcType=TIMESTAMP}

mapper.delete:
delete from SYS_APPLICATION
where (
ID IN

	#{x_rowId}

)

mapper.select:
select E.*
order by ${orderBy}
order by E.LOCKED asc, E.PARENTID, E.SORTNO asc, E.TREEID asc

mapper.sql:
from SYS_APPLICATION E


	1 = 1


		and E.PARENTID = #{parentId}



		and E.PARENTID IN

			#{x_parentId}




		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and ( E.NAME like #{namePinyinLike} or E.NAMEPINYIN
		like
		#{namePinyinLike} )



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.APPDESC like #{descLike}



		and E.TREEID like #{treeIdLeftLike}



		and E.TREEID like #{treeIdLike}



		and E.TREEID like #{treeIdRightLike}



		and E.URL like #{urlLike}



		and E.SORTNO >= #{sortGreaterThanOrEqual}



		and E.SORTNO > #{sortGreaterThan}



		and E.SORTNO <= #{sortLessThanOrEqual}



		and E.SORTNO < #{sortLessThan}



		and E.SHOWMENU = #{showMenu}



		and E.SYSFLAG_ = #{sysFlag}



		and E.LOCKED is not null
		and E.LOCKED = #{locked}



		and E.DELETEFLAG = #{deleteFlag}
