		,#{tenantId, jdbcType=VARCHAR}


		,#{treeId, jdbcType=VARCHAR}


		,#{icon, jdbcType=VARCHAR}


		,#{iconCls, jdbcType=VARCHAR}


		,#{type, jdbcType=VARCHAR}


		,#{url, jdbcType=VARCHAR}


