		,#{createTime, jdbcType=TIMESTAMP}


		,#{updateBy, jdbcType=VARCHAR}


		,#{updateDate, jdbcType=TIMESTAMP}


		,#{deleteFlag, jdbcType=INTEGER}


		,#{deleteTime, jdbcType=TIMESTAMP}

mapper.delete:
delete from SYS_ORGANIZATION
where (
ID IN

	#{x_rowId}

)

mapper.select:
select E.*
order by ${orderBy}
order by E.LOCKED asc, E.PARENTID, E.SORTNO asc, E.TREEID asc

mapper.sql:
from SYS_ORGANIZATION E


	1 = 1


		and E.PARENTID = #{parentId}



		and E.PARENTID IN

			#{x_parentId}




		and E.TENANTID = #{tenantId}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and ( E.NAME like #{namePinyinLike} or E.NAMEPINYIN
		like #{namePinyinLike} )



		and E.ORG_DESC like #{descriptionLike}



		and E.CREATETIME >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME <= #{createTimeLessThanOrEqual}



		and E.SORTNO >= #{sortGreaterThanOrEqual}



		and E.SORTNO <= #{sortLessThanOrEqual}



		and E.ORG_NO = #{no}



		and E.ORG_NO like #{noLike}



		and E.ORG_NO like #{noLeftLike}



		and E.ORG_NO like #{noRightLike}



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.CODE like #{codeLeftLike}



		and E.CODE like #{codeRightLike}



		and E.CODE2 = #{code2}



		and E.CODE like #{code2Like}



		and E.CODE like #{code2LeftLike}



		and E.CODE like #{code2RightLike}



		and E.LOCKED = #{locked}



		and E.TYPE = #{type}



		and E.DELETEFLAG = #{deleteFlag}



		and E.FINCODE = #{fincode}



		and E.FINCODE like #{fincodeLike}



		and E.FINCODE like #{fincodeLeftLike}



		and E.FINCODE like #{fincodeRightLike}



		and E.TREEID like #{treeIdLeftLike}



		and E.TREEID like #{treeIdLike}



		and E.TREEID like #{treeIdRightLike}



		and E.ID IN

			#{x_organizationId}




		and E.LEVEL = #{level}



		and E.LEVEL >= #{levelGreaterThanOrEqual}



		and E.LEVEL <= #{levelLessThanOrEqual}
