SELECT * FROM dual

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.ID_ desc

mapper.sql:
from SYS_TENANT E


	1 = 1


		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and ( E.NAME_ like #{namePinyinLike} or E.NAMEPINYIN_
		like #{namePinyinLike} )



		and E.CODE_ = #{code}



		and E.CODE_ like #{codeLike}



		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.TENANTTYPE_ = #{tenantType}



		and E.DATABASEID_ = #{databaseId}



		and E.DATABASEID_ IN

			#{x_databaseId}




		and E.LEVEL_ = #{level}



		and E.LEVEL_ >= #{levelGreaterThanOrEqual}



		and E.LEVEL_ <= #{levelLessThanOrEqual}



		and E.PROVINCE_ = #{province}



		and E.PROVINCEID_ = #{provinceId}



		and E.CITY_ = #{city}



		and E.CITYID_ = #{cityId}



		and E.CITYID_ IN

			#{x_cityId}




		and E.AREA_ = #{area}



		and E.AREAID_ = #{areaId}



		and E.AREAID_ IN

			#{x_areaId}




		and E.TOWN_ = #{town}



		and E.TOWNID_ = #{townId}



		and E.TOWNID_ IN

			#{x_townId}




		and E.ADDRESS_ like #{addressLike}



		and E.PROPERTY_ = #{property}



		and E.VERIFY_ = #{verify}



		and E.TICKETFLAG_ = #{ticketFlag}



		and E.TYPE_ = #{type}



		and E.LOCKED_ = #{locked}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.UPDATETIME_ >= #{updateTimeGreaterThanOrEqual}



		and E.UPDATETIME_ <= #{updateTimeLessThanOrEqual}
