SELECT * FROM dual

mapper.delete:
delete from HEALTH_FOOD_COMPOSITION
where (
ID_ IN

	#{x_id}

)

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.NODEID_ asc, E.SORTNO_ asc

mapper.sql:
from HEALTH_FOOD_COMPOSITION E


	1 = 1


		and E.ID_ IN

			#{x_id}




		and E.TENANTID_ = #{tenantId}



		and E.NODEID_ = #{nodeId}



		and E.NODEID_ IN

			#{x_nodeId}




		and E.TREEID_ = #{treeId}



		and E.TREEID_ like #{treeIdLike}



		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and E.ALIAS_ = #{alias}



		and E.ALIAS_ like #{aliasLike}



		and E.CODE_ = #{code}



		and E.CODE_ like #{codeLike}



		and E.DISCRIMINATOR_ = #{discriminator}



		and E.DESCRIPTION_ like #{descriptionLike}



		and E.RADICAL_ >= #{radicalGreaterThanOrEqual}



		and E.RADICAL_ <= #{radicalLessThanOrEqual}



		and E.HEATENERGY_ >= #{heatEnergyGreaterThanOrEqual}



		and E.HEATENERGY_ <= #{heatEnergyLessThanOrEqual}



		and E.PROTEIN_ >= #{proteinGreaterThanOrEqual}



		and E.PROTEIN_ <= #{proteinLessThanOrEqual}



		and E.FAT_ >= #{fatGreaterThanOrEqual}



		and E.FAT_ <= #{fatLessThanOrEqual}



		and E.CARBOHYDRATE_ >=
		#{carbohydrateGreaterThanOrEqual}



		and E.CARBOHYDRATE_ <= #{carbohydrateLessThanOrEqual}



		and E.VITAMINA_ >= #{vitaminAGreaterThanOrEqual}



		and E.VITAMINA_ <= #{vitaminALessThanOrEqual}



		and E.VITAMINB1_ >= #{vitaminB1GreaterThanOrEqual}



		and E.VITAMINB1_ <= #{vitaminB1LessThanOrEqual}



		and E.VITAMINB2_ >= #{vitaminB2GreaterThanOrEqual}



		and E.VITAMINB2_ <= #{vitaminB2LessThanOrEqual}



		and E.VITAMINB6_ >= #{vitaminB6GreaterThanOrEqual}



		and E.VITAMINB6_ <= #{vitaminB6LessThanOrEqual}



		and E.VITAMINB12_ >= #{vitaminB12GreaterThanOrEqual}



		and E.VITAMINB12_ <= #{vitaminB12LessThanOrEqual}



		and E.VITAMINC_ >= #{vitaminCGreaterThanOrEqual}



		and E.VITAMINC_ <= #{vitaminCLessThanOrEqual}



		and E.CAROTENE_ >= #{caroteneGreaterThanOrEqual}



		and E.CAROTENE_ <= #{caroteneLessThanOrEqual}



		and E.RETINOL_ >= #{retinolGreaterThanOrEqual}



		and E.RETINOL_ <= #{retinolLessThanOrEqual}



		and E.NICOTINICCID_ >=
		#{nicotinicCidGreaterThanOrEqual}



		and E.NICOTINICCID_ <= #{nicotinicCidLessThanOrEqual}



		and E.CALCIUM_ >= #{calciumGreaterThanOrEqual}



		and E.CALCIUM_ <= #{calciumLessThanOrEqual}



		and E.IRON_ >= #{ironGreaterThanOrEqual}



		and E.IRON_ <= #{ironLessThanOrEqual}



		and E.ZINC_ >= #{zincGreaterThanOrEqual}



		and E.ZINC_ <= #{zincLessThanOrEqual}



		and E.SYSFLAG_ = #{sysFlag}



		and E.USEFLAG_ = #{useFlag}



		and E.DAILYFLAG_ = #{dailyFlag}



		and E.ENABLEFLAG_ = #{enableFlag}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.UPDATETIME_ >= #{updateTimeGreaterThanOrEqual}



		and E.UPDATETIME_ <= #{updateTimeLessThanOrEqual}
