SELECT * FROM dual

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.PURCHASE_TIME_ desc

mapper.sql:
from GOODS_PURCHASE${tableSuffix} E


	1 = 1


		and E.ID_ IN

			#{x_id}




		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.GOODSID_ = #{goodsId}



		and E.GOODSID_ IN

			#{x_goodsId}




		and E.GOODSNAME_ like #{goodsNameLike}



		and E.GOODSNODEID_ = #{goodsNodeId}



		and E.PURCHASE_TIME_ >=
		#{purchaseTimeGreaterThanOrEqual}



		and E.PURCHASE_TIME_ <= #{purchaseTimeLessThanOrEqual}



		and E.SEMESTER_ = #{semester}



		and E.YEAR_ = #{year}



		and E.MONTH_ = #{month}



		and E.DAY_ = #{day}



		and E.FULLDAY_ = #{fullDay}



		and E.FULLDAY_ >= #{fullDayGreaterThanOrEqual}



		and E.FULLDAY_ <= #{fullDayLessThanOrEqual}



		and E.WEEK_ = #{week}



		and E.QUANTITY_ >= #{quantityGreaterThanOrEqual}



		and E.QUANTITY_ <= #{quantityLessThanOrEqual}



		and E.UNIT_ = #{unit}



		and E.PRICE_ >= #{priceGreaterThanOrEqual}



		and E.PRICE_ <= #{priceLessThanOrEqual}



		and E.TOTALPRICE_ >= #{totalPriceGreaterThanOrEqual}



		and E.TOTALPRICE_ <= #{totalPriceLessThanOrEqual}



		and E.PROPOSERID_ = #{proposerId}



		and E.PROPOSERID_ IN

			#{x_proposerId}




		and E.PROPOSERNAME_ like #{proposerNameLike}



		and E.BATCHNO_ like #{batchNoLike}



		and E.CONTACT_ like #{contactLike}



		and E.STANDARD_ = #{standard}



		and E.ADDRESS_ like #{addressLike}



		and E.REMARK_ like #{remarkLike}



		and E.BUSINESSSTATUS_ = #{businessStatus}



		and E.CONFIRMBY_ = #{confirmBy}



		and E.CONFIRMTIME_ >= #{confirmTimeGreaterThanOrEqual}



		and E.CONFIRMTIME_ <= #{confirmTimeLessThanOrEqual}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
