mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.LOCKED_ asc, E.SORTNO_ asc, E.LEVEL_ asc

mapper.sql:
from HEALTH_GRADE_INFO E


	1 = 1


		and E.ORGANIZATIONID_ = #{organizationId}



		and E.TENANTID_ = #{tenantId}



		and E.ID_ IN

			#{x_gradeId}




		and E.NAME_ like #{nameLike}



		and E.CODE_ = #{code}



		and E.CODE_ like #{codeLike}



		and E.LEVEL_ = #{level}



		and E.PRINCIPAL_ like #{principalLike}



		and E.TYPE_ = #{type}



		and E.YEAR_ = #{year}



		and E.REMARK_ like #{remarkLike}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.UPDATETIME_ >= #{updateTimeGreaterThanOrEqual}



		and E.UPDATETIME_ <= #{updateTimeLessThanOrEqual}



		and E.LOCKED_ = #{locked}



		and E.DELETEFLAG_ = #{deleteFlag}
