SELECT * FROM dual

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.CHECKDATE_ desc

mapper.sql:
from HEALTH_MEDICAL_EXAMINATION E


	1 = 1


		and E.BATCHID_ = #{batchId}



		and E.CHECKID_ = #{checkId}



		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.GRADEID_ = #{gradeId}



		and E.GRADEID_ IN

			#{x_gradeId}




		and E.PERSONID_ = #{personId}



		and E.PERSONID_ IN

			#{x_personId}




		and E.NAME_ like #{nameLike}



		and E.SEX_ = #{sex}



		and E.HEIGHT_ >= #{heightGreaterThanOrEqual}



		and E.HEIGHT_ <= #{heightLessThanOrEqual}



		and E.WEIGHT_ >= #{weightGreaterThanOrEqual}



		and E.WEIGHT_ <= #{weightLessThanOrEqual}



		and E.WEIGHTHEIGHTPERCENT_ >=
		#{weightHeightPercentGreaterThanOrEqual}



		and E.WEIGHTHEIGHTPERCENT_ <=
		#{weightHeightPercentLessThanOrEqual}



		and E.BMI_ >= #{bmiGreaterThanOrEqual}



		and E.BMI_ <= #{bmiLessThanOrEqual}



		and E.ALLERGY_ = #{allergy}



		and E.ALLERGY_ like #{allergyLike}



		and E.EYELEFT_ = #{eyeLeft}



		and E.EYELEFT_ like #{eyeLeftLike}



		and E.EYERIGHT_ = #{eyeRight}



		and E.EYERIGHT_ like #{eyeRightLike}



		and E.EYESIGHTLEFT_ >=
		#{eyesightLeftGreaterThanOrEqual}



		and E.EYESIGHTLEFT_ <= #{eyesightLeftLessThanOrEqual}



		and E.EYESIGHTRIGHT_ >=
		#{eyesightRightGreaterThanOrEqual}



		and E.EYESIGHTRIGHT_ <=
		#{eyesightRightLessThanOrEqual}



		and E.EARLEFT_ = #{earLeft}



		and E.EARLEFT_ like #{earLeftLike}



		and E.EARRIGHT_ = #{earRight}



		and E.EARRIGHT_ like #{earRightLike}



		and E.SAPRODONTIA_ = #{saprodontia}



		and E.SAPRODONTIA_ >= #{saprodontiaGreaterThanOrEqual}



		and E.SAPRODONTIA_ <= #{saprodontiaLessThanOrEqual}



		and E.HEAD_ = #{head}



		and E.HEAD_ like #{headLike}



		and E.THORAX_ = #{thorax}



		and E.THORAX_ like #{thoraxLike}



		and E.SPINE_ = #{spine}



		and E.SPINE_ like #{spineLike}



		and E.PHARYNGEAL_ = #{pharyngeal}



		and E.PHARYNGEAL_ like #{pharyngealLike}



		and E.CARDIOPULMONARY_ = #{cardiopulmonary}



		and E.CARDIOPULMONARY_ like #{cardiopulmonaryLike}



		and E.HEPATOLIENAL_ = #{hepatolienal}



		and E.HEPATOLIENAL_ like #{hepatolienalLike}



		and E.PUDENDUM_ = #{pudendum}



		and E.PUDENDUM_ like #{pudendumLike}



		and E.SKIN_ = #{skin}



		and E.SKIN_ like #{skinLike}



		and E.HEMOGLOBIN_ = #{hemoglobin}



		and E.HEMOGLOBIN_ like #{hemoglobinLike}



		and E.ALT_ = #{alt}



		and E.ALT_ like #{altLike}



		and E.TYPE_ = #{type}



		and E.CHECKDATE_ >= #{checkDateGreaterThanOrEqual}



		and E.CHECKDATE_ <= #{checkDateLessThanOrEqual}



		and E.CHECKDOCTOR_ = #{checkDoctor}



		and E.CHECKDOCTOR_ like #{checkDoctorLike}



		and E.CHECKDOCTORID_ = #{checkDoctorId}



		and E.CHECKDOCTORID_ like #{checkDoctorIdLike}



		and E.CHECKRESULT_ = #{checkResult}



		and E.CHECKRESULT_ like #{checkResultLike}



		and E.CHECKORGANIZATION_ = #{checkOrganization}



		and E.CHECKORGANIZATION_ like #{checkOrganizationLike}



		and E.CHECKORGANIZATIONID_ = #{checkOrganizationId}



		and E.CHECKORGANIZATIONID_ IN

			#{x_checkOrganizationId}




		and E.DOCTORSUGGEST_ = #{doctorSuggest}



		and E.DOCTORSUGGEST_ like #{doctorSuggestLike}



		and E.YEAR_ = #{year}



		and E.YEAR_ IN

			#{x_year}




		and E.MONTH_ = #{month}



		and E.MONTH_ IN

			#{x_month}




		and E.REMARK_ like #{remarkLike}



		and E.CONFIRMBY_ = #{confirmBy}



		and E.CONFIRMTIME_ >= #{confirmTimeGreaterThanOrEqual}



		and E.CONFIRMTIME_ <= #{confirmTimeLessThanOrEqual}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
