SELECT * FROM dual

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.CREATETIME_ desc

mapper.sql:
from HEALTH_PERSON_TRANSFER_SCHOOL E


	1 = 1


		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.FROM_TENANTID_ = #{fromTenantId}



		and E.FROM_SCHOOL_ like #{fromSchoolLike}



		and E.TO_TENANTID_ = #{toTenantId}



		and E.TO_SCHOOL_ like #{toSchoolLike}



		and E.PERSONID_ = #{personId}



		and E.PERSONID_ IN

			#{x_personId}




		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and E.CHECKDATE_ >= #{checkDateGreaterThanOrEqual}



		and E.CHECKDATE_ <= #{checkDateLessThanOrEqual}



		and E.REMARK_ like #{remarkLike}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
