mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.ID_ desc

mapper.sql:
from HEALTH_PHYSICAL_GROWTH_COUNT E


	1 = 1


		and E.CHECKID_ = #{checkId}



		and E.CHECKID_ IN

			#{x_checkId}




		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.GRADEID_ = #{gradeId}



		and E.GRADEID_ IN

			#{x_gradeId}




		and E.TYPE_ = #{type}
